import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  // setup state
  const [people, setPeople] = useState([]);
  const [error, setError] = useState("");
  const [hobbies, setHobbies] = useState([]);

  const fetchPeople = async() => {
    try {      
      const response = await axios.get("/api/people");
      setPeople(response.data);
    } catch(error) {
      setError("error retrieving tasks: " + error);
    }
  }
  const fetchHobbies = async() => {
    try {      
      const response = await axios.get("/api/hobbies");
      setHobbies(response.data);
    } catch(error) {
      setError("error retrieving tasks: " + error);
    }
  }
  /*const createTask = async() => {
    try {
      await axios.post("/api/todo", {task: item, completed: false});
    } catch(error) {
      setError("error adding a task: " + error);
    }
  }
  const deleteOneTask = async(task) => {
    try {
      await axios.delete("/api/todo/" + task.id);
    } catch(error) {
      setError("error deleting a task" + error);
    }
  }
  const toggleOneTask = async(task) => {
    try {
      if(task.completed === true) {
        task.completed = false;
      } else {
        task.completed = true;
      }
      await axios.put("/api/todo/" + task.id, task);
    } catch(error) {
      setError("error modifying a task" + error);
    }
  }*/

  // fetch ticket data
  useEffect(() => {
    fetchPeople();
    fetchHobbies();
  },[]);

  const getHobby = async(name) => {
    //fetchHobbies();
    return hobbies.filter(hobby => hobby.name == name)[0];
  }


  // render results
  return (
    <div className="App">
      {error}
      <h1>Check out these creative people!</h1>
      <div class="item">
        {people.map( person => (
          <div class="item-container">
            <div key={person.id} class="item-body">
              {person.firstName} {person.lastName}
            </div>
            <div key={person.id} class="item-body2">
              Favorite color is
            </div>
            <div key={person.id} class="item-body">
              {person.favColor}
            </div>
            <div key={person.id} class="item-body2">
              Favorite hobbie is
            </div>
            <div key={person.id} class="item-body" onClick={getHobby(person.hobby)}>
              {person.hobby} 
            </div>
            <div key={person.id} class="item-body" onClick={getHobby(person.hobby)}> 
              <button onClick={e => getHobby(person.hobby)}>View Hobby Details</button>
            </div>
          </div>
        ))}     
      </div>
    </div>
  );
}

export default App;
